import React from 'react';
import { render, screen } from '@testing-library/react';
import Button from './Button';
import { ThemeProvider } from '../../../contexts/ThemeContext';

const renderWithTheme = (ui: React.ReactElement) => {
  return render(<ThemeProvider>{ui}</ThemeProvider>);
};

describe('Button', () => {
  it('renders correctly', () => {
    renderWithTheme(<Button>Test Button</Button>);
    expect(screen.getByText('Test Button')).toBeInTheDocument();
  });

  it('applies variant correctly', () => {
    renderWithTheme(<Button variant="contained">Contained Button</Button>);
    const button = screen.getByText('Contained Button');
    expect(button).toHaveClass('MuiButton-contained');
  });

  it('applies color correctly', () => {
    renderWithTheme(<Button color="secondary">Secondary Button</Button>);
    const button = screen.getByText('Secondary Button');
    expect(button).toHaveClass('MuiButton-colorSecondary');
  });
});

import React from 'react';
import { Button as MuiButton, ButtonProps as MuiButtonProps } from '@mui/material';

export interface ButtonProps extends MuiButtonProps {
  children: React.ReactNode;
  loading?: boolean;
}

const Button: React.FC<ButtonProps> = ({ children, loading, ...props }) => {
  return <MuiButton {...props}>{children}</MuiButton>;
};

export default Button;

import React from 'react';
import { render, screen } from '@testing-library/react';
import Card from './Card';
import { ThemeProvider } from '../../../contexts/ThemeContext';
import { Button } from '@mui/material';

const renderWithTheme = (ui: React.ReactElement) => {
  return render(<ThemeProvider>{ui}</ThemeProvider>);
};

describe('Card', () => {
  it('renders title correctly', () => {
    renderWithTheme(<Card title="Test Title" />);
    expect(screen.getByText('Test Title')).toBeInTheDocument();
  });

  it('renders subtitle correctly', () => {
    renderWithTheme(<Card subtitle="Test Subtitle" />);
    expect(screen.getByText('Test Subtitle')).toBeInTheDocument();
  });

  it('renders description correctly', () => {
    renderWithTheme(<Card description="Test Description" />);
    expect(screen.getByText('Test Description')).toBeInTheDocument();
  });

  it('renders children correctly', () => {
    renderWithTheme(
      <Card>
        <div data-testid="child-element">Child Content</div>
      </Card>
    );
    expect(screen.getByTestId('child-element')).toBeInTheDocument();
    expect(screen.getByText('Child Content')).toBeInTheDocument();
  });

  it('renders action correctly', () => {
    renderWithTheme(<Card action={<Button>Action Button</Button>} />);
    expect(screen.getByText('Action Button')).toBeInTheDocument();
  });

  it('renders image when provided', () => {
    renderWithTheme(<Card image="/test-image.jpg" imageAlt="Test Image" />);
    const image = screen.getByAltText('Test Image');
    expect(image).toBeInTheDocument();
    expect(image).toHaveAttribute('src', '/test-image.jpg');
  });
});

import React from 'react';
import {
  Card as MuiCard,
  CardProps as MuiCardProps,
  CardContent,
  CardMedia,
  Typography,
  Box,
} from '@mui/material';

export interface CardProps extends MuiCardProps {
  title?: string;
  subtitle?: string;
  description?: string;
  image?: string;
  imageAlt?: string;
  imageHeight?: number;
  children?: React.ReactNode;
  action?: React.ReactNode;
}

const Card: React.FC<CardProps> = ({
  title,
  subtitle,
  description,
  image,
  imageAlt,
  imageHeight = 200,
  children,
  action,
  ...props
}) => {
  return (
    <MuiCard
      {...props}
      sx={{ height: '100%', display: 'flex', flexDirection: 'column', ...props.sx }}
    >
      {image && (
        <CardMedia
          component="img"
          height={imageHeight}
          image={image}
          alt={imageAlt || title || 'Card image'}
        />
      )}
      <CardContent sx={{ flexGrow: 1 }}>
        {title && (
          <Typography variant="h6" component="h3" gutterBottom>
            {title}
          </Typography>
        )}
        {subtitle && (
          <Typography variant="subtitle1" color="text.secondary" gutterBottom>
            {subtitle}
          </Typography>
        )}
        {description && (
          <Typography variant="body2" color="text.secondary" paragraph>
            {description}
          </Typography>
        )}
        {children}
        {action && <Box sx={{ mt: 2 }}>{action}</Box>}
      </CardContent>
    </MuiCard>
  );
};

export default Card;

import React from 'react';
import { render, screen } from '@testing-library/react';
import { BrowserRouter } from 'react-router-dom';
import PageHeader from './PageHeader';
import { ThemeProvider } from '../../../contexts/ThemeContext';
import { Button } from '@mui/material';

const renderWithTheme = (ui: React.ReactElement) => {
  return render(
    <BrowserRouter>
      <ThemeProvider>{ui}</ThemeProvider>
    </BrowserRouter>
  );
};

describe('PageHeader', () => {
  it('renders title correctly', () => {
    renderWithTheme(<PageHeader title="Test Title" />);
    expect(screen.getByText('Test Title')).toBeInTheDocument();
  });

  it('renders subtitle correctly', () => {
    renderWithTheme(<PageHeader title="Test Title" subtitle="Test Subtitle" />);
    expect(screen.getByText('Test Subtitle')).toBeInTheDocument();
  });

  it('renders action correctly', () => {
    renderWithTheme(
      <PageHeader title="Test Title" action={<Button data-testid="action-button">Action</Button>} />
    );
    expect(screen.getByTestId('action-button')).toBeInTheDocument();
  });

  it('renders breadcrumbs correctly', () => {
    const breadcrumbs = [
      { label: 'Home', path: '/' },
      { label: 'Category', path: '/category' },
      { label: 'Current Page' },
    ];

    renderWithTheme(<PageHeader title="Test Title" breadcrumbs={breadcrumbs} />);

    expect(screen.getByText('Home')).toBeInTheDocument();
    expect(screen.getByText('Category')).toBeInTheDocument();
    expect(screen.getByText('Current Page')).toBeInTheDocument();
  });

  it('applies custom styles correctly', () => {
    renderWithTheme(
      <PageHeader
        title="Test Title"
        titleSx={{ color: 'red' }}
        subtitle="Test Subtitle"
        subtitleSx={{ fontStyle: 'italic' }}
      />
    );

    const title = screen.getByText('Test Title');
    const subtitle = screen.getByText('Test Subtitle');

    expect(title).toHaveStyle('color: red');
    expect(subtitle).toHaveStyle('font-style: italic');
  });
});

import React from 'react';
import { Typography, Box, Breadcrumbs, Link, BoxProps, SxProps, Theme } from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';
import { NavigateNext as NavigateNextIcon } from '@mui/icons-material';

export interface BreadcrumbItem {
  label: string;
  path?: string;
}

export interface PageHeaderProps extends BoxProps {
  title: string;
  subtitle?: string;
  breadcrumbs?: BreadcrumbItem[];
  action?: React.ReactNode;
  titleSx?: SxProps<Theme>;
  subtitleSx?: SxProps<Theme>;
}

const PageHeader: React.FC<PageHeaderProps> = ({
  title,
  subtitle,
  breadcrumbs,
  action,
  titleSx,
  subtitleSx,
  ...props
}) => {
  return (
    <Box {...props} sx={{ mb: 4, ...props.sx }}>
      {breadcrumbs && breadcrumbs.length > 0 && (
        <Breadcrumbs
          separator={<NavigateNextIcon fontSize="small" />}
          aria-label="breadcrumb"
          sx={{ mb: 2 }}
        >
          {breadcrumbs.map((item, index) => {
            const isLast = index === breadcrumbs.length - 1;

            return isLast ? (
              <Typography key={item.label} color="text.primary">
                {item.label}
              </Typography>
            ) : (
              <Link
                key={item.label}
                component={RouterLink}
                to={item.path || '#'}
                color="inherit"
                underline="hover"
              >
                {item.label}
              </Link>
            );
          })}
        </Breadcrumbs>
      )}

      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
        <Box>
          <Typography
            variant="h4"
            component="h1"
            gutterBottom
            data-testid="page-title"
            sx={titleSx}
          >
            {title}
          </Typography>

          {subtitle && (
            <Typography variant="body1" color="text.secondary" paragraph sx={subtitleSx}>
              {subtitle}
            </Typography>
          )}
        </Box>

        {action && <Box sx={{ ml: 2 }}>{action}</Box>}
      </Box>
    </Box>
  );
};

export default PageHeader;

import React from 'react';
import { render, screen } from '@testing-library/react';
import Section from './Section';
import { ThemeProvider } from '../../../contexts/ThemeContext';
import { InfoOutlined } from '@mui/icons-material';

const renderWithTheme = (ui: React.ReactElement) => {
  return render(<ThemeProvider>{ui}</ThemeProvider>);
};

describe('Section', () => {
  it('renders title correctly', () => {
    renderWithTheme(<Section title="Test Title" />);
    expect(screen.getByText('Test Title')).toBeInTheDocument();
  });

  it('renders subtitle correctly', () => {
    renderWithTheme(<Section subtitle="Test Subtitle" />);
    expect(screen.getByText('Test Subtitle')).toBeInTheDocument();
  });

  it('renders children correctly', () => {
    renderWithTheme(
      <Section>
        <div data-testid="child-element">Child Content</div>
      </Section>
    );
    expect(screen.getByTestId('child-element')).toBeInTheDocument();
    expect(screen.getByText('Child Content')).toBeInTheDocument();
  });

  it('renders title icon when provided', () => {
    renderWithTheme(
      <Section title="Test Title" titleIcon={<InfoOutlined data-testid="title-icon" />} />
    );
    expect(screen.getByTestId('title-icon')).toBeInTheDocument();
  });

  it('renders divider when divider prop is true', () => {
    const { container } = renderWithTheme(<Section title="Test Title" divider />);
    // Check for the presence of a divider element
    expect(container.querySelector('hr')).toBeInTheDocument();
  });

  it('does not render divider when divider prop is false', () => {
    const { container } = renderWithTheme(<Section title="Test Title" divider={false} />);
    // Check that there is no divider element
    expect(container.querySelector('hr')).not.toBeInTheDocument();
  });
});

import React from 'react';
import { Box, Typography, Divider, BoxProps, SxProps, Theme } from '@mui/material';

export interface SectionProps extends BoxProps {
  title?: string;
  subtitle?: string;
  titleIcon?: React.ReactNode;
  divider?: boolean;
  titleSx?: SxProps<Theme>;
  subtitleSx?: SxProps<Theme>;
  contentSx?: SxProps<Theme>;
}

const Section: React.FC<SectionProps> = ({
  title,
  subtitle,
  titleIcon,
  divider = false,
  children,
  titleSx,
  subtitleSx,
  contentSx,
  ...props
}) => {
  return (
    <Box component="section" {...props} sx={{ mb: 4, ...props.sx }}>
      {title && (
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          {titleIcon && <Box sx={{ mr: 1 }}>{titleIcon}</Box>}
          <Typography variant="h5" component="h2" sx={titleSx}>
            {title}
          </Typography>
        </Box>
      )}

      {subtitle && (
        <Typography variant="subtitle1" color="text.secondary" paragraph sx={subtitleSx}>
          {subtitle}
        </Typography>
      )}

      {divider && <Divider sx={{ mb: 2 }} />}

      <Box sx={contentSx}>{children}</Box>
    </Box>
  );
};

export default Section;

import React from 'react';
import { render, screen } from '@testing-library/react';
import { BrowserRouter } from 'react-router-dom';
import { ThemeProvider } from '../../../contexts/ThemeContext';
import Footer from './Footer';

const renderWithProviders = (ui: React.ReactElement) => {
  return render(
    <BrowserRouter>
      <ThemeProvider>{ui}</ThemeProvider>
    </BrowserRouter>
  );
};

describe('Footer', () => {
  it('renders the footer with logo and description', () => {
    renderWithProviders(<Footer />);

    expect(screen.getByText('Houston Guide')).toBeInTheDocument();
    expect(
      screen.getByText(/Your comprehensive guide for the FIRST Championship 2025/i)
    ).toBeInTheDocument();
  });

  it('renders social media icons', () => {
    renderWithProviders(<Footer />);

    expect(screen.getByLabelText('facebook')).toBeInTheDocument();
    expect(screen.getByLabelText('twitter')).toBeInTheDocument();
    expect(screen.getByLabelText('instagram')).toBeInTheDocument();
    expect(screen.getByLabelText('github')).toBeInTheDocument();
  });

  it('renders navigation sections with links', () => {
    renderWithProviders(<Footer />);

    // Check section titles
    expect(screen.getByText('Main Pages')).toBeInTheDocument();
    expect(screen.getByText('Annexes')).toBeInTheDocument();
    expect(screen.getByText('Resources')).toBeInTheDocument();

    // Check some links from each section
    expect(screen.getByText('Home')).toBeInTheDocument();
    expect(screen.getByText('Attractions')).toBeInTheDocument();
    expect(screen.getByText('Transport Maps')).toBeInTheDocument();
    expect(screen.getByText('Emergency Contacts')).toBeInTheDocument();
    expect(screen.getByText('Thematic Index')).toBeInTheDocument();
    expect(screen.getByText('Itineraries')).toBeInTheDocument();
  });

  it('renders feedback section', () => {
    renderWithProviders(<Footer />);

    expect(screen.getByText('Feedback')).toBeInTheDocument();
    expect(screen.getByText('Send Feedback')).toBeInTheDocument();
  });

  it('renders copyright and legal links', () => {
    renderWithProviders(<Footer />);

    const currentYear = new Date().getFullYear();
    expect(
      screen.getByText(`© ${currentYear} Houston Guide. All rights reserved.`)
    ).toBeInTheDocument();
    expect(screen.getByText('Privacy Policy')).toBeInTheDocument();
    expect(screen.getByText('Terms of Use')).toBeInTheDocument();
  });
});

import React from 'react';
import {
  Box,
  Container,
  Typography,
  Grid,
  Link,
  Divider,
  IconButton,
  useTheme,
} from '@mui/material';
