import React, { useRef, useEffect, useState } from 'react';
import { Box, CircularProgress, Typography } from '@mui/material';

// Define the props for the GoogleMap component
export interface GoogleMapProps {
  center?: google.maps.LatLngLiteral;
  zoom?: number;
  mapId?: string;
  mapContainerStyle?: React.CSSProperties;
  options?: google.maps.MapOptions;
  onMapLoad?: (map: google.maps.Map) => void;
  children?: React.ReactNode;
}

// Define the default center coordinates (Houston)
const defaultCenter = { lat: 29.7604, lng: -95.3698 };

const GoogleMap: React.FC<GoogleMapProps> = ({
  center = defaultCenter,
  zoom = 12,
  mapId,
  mapContainerStyle = { width: '100%', height: '500px' },
  options,
  onMapLoad,
  children,
}) => {
  // Create a ref to store the map instance
  const mapRef = useRef<HTMLDivElement | null>(null);
  const [map, setMap] = useState<google.maps.Map | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Initialize the map when the component mounts
  useEffect(() => {
    // Check if the Google Maps API is loaded
    if (!window.google || !window.google.maps) {
      setError('Google Maps API not loaded. Please check your API key.');
      setLoading(false);
      return;
    }

    try {
      // Create the map instance
      if (mapRef.current && !map) {
        const mapOptions: google.maps.MapOptions = {
          center,
          zoom,
          mapId,
          ...options,
        };

        const newMap = new window.google.maps.Map(mapRef.current, mapOptions);
        setMap(newMap);
        setLoading(false);

        // Call the onMapLoad callback if provided
        if (onMapLoad) {
          onMapLoad(newMap);
        }
      }
    } catch (err) {
      console.error('Error initializing Google Map:', err);
      setError('Failed to initialize Google Map. Please try again later.');
      setLoading(false);
    }
  }, [center, zoom, mapId, options, onMapLoad, map]);

  // Render loading state
  if (loading) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: mapContainerStyle.height || '500px',
          width: mapContainerStyle.width || '100%',
          bgcolor: 'background.paper',
        }}
      >
        <CircularProgress />
      </Box>
    );
  }

  // Render error state
  if (error) {
    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          height: mapContainerStyle.height || '500px',
          width: mapContainerStyle.width || '100%',
          bgcolor: 'background.paper',
          color: 'error.main',
          border: '1px solid',
          borderColor: 'error.main',
          borderRadius: 1,
          p: 2,
        }}
      >
        <Typography variant="body1" color="error">
          {error}
        </Typography>
      </Box>
    );
  }

  // Render the map
  return (
    <Box
      sx={{
        position: 'relative',
        width: '100%',
        height: '100%',
      }}
    >
      <Box
        ref={mapRef}
        sx={{
          width: mapContainerStyle.width || '100%',
          height: mapContainerStyle.height || '500px',
          borderRadius: 1,
          overflow: 'hidden',
        }}
        data-testid="google-map-container"
      />
      {/* Render children (markers, etc.) */}
      {map && children}
    </Box>
  );
};

export default GoogleMap;

