import React, { useEffect, useState } from 'react';

export interface MarkerProps {
  position: google.maps.LatLngLiteral;
  map: google.maps.Map;
  title?: string;
  icon?: string;
  onClick?: (marker: google.maps.marker.AdvancedMarkerElement) => void;
  children?: React.ReactNode;
}

const Marker: React.FC<MarkerProps> = ({ position, map, title, icon, onClick, children }) => {
  const [marker, setMarker] = useState<google.maps.marker.AdvancedMarkerElement | null>(null);

  useEffect(() => {
    if (!map || !window.google) return;

    // Check if the AdvancedMarkerElement class is available
    if (!google.maps.marker || !google.maps.marker.AdvancedMarkerElement) {
      console.error(
        'AdvancedMarkerElement is not available. Make sure you have the latest version of the Google Maps JavaScript API.'
      );
      return;
    }

    // Create marker options
    const markerOptions: google.maps.marker.AdvancedMarkerElementOptions = {
      position,
      map,
      title,
      icon,
    };

    // Create the marker
    const newMarker = new google.maps.marker.AdvancedMarkerElement(markerOptions);
    setMarker(newMarker);

    // Add click event listener
    if (onClick) {
      newMarker.addListener('click', () => onClick(newMarker));
    }

    // Clean up the marker when the component unmounts
    return () => {
      if (newMarker) {
        newMarker.map = null;
      }
    };
  }, [map, position, title, icon, onClick]);

  // The marker is rendered by the Google Maps API, so we don't need to return any JSX
  return null;
};

export default Marker;

import React, { createContext, useState, useContext, useEffect, useMemo } from 'react';
import { ThemeProvider as MuiThemeProvider } from '@mui/material/styles';
import type { PaletteMode } from '@mui/material';
import { getTheme } from '../theme';

// Define the context type
interface ThemeContextType {
  mode: PaletteMode;
  toggleColorMode: () => void;
}

// Create the context with default values
const ThemeContext = createContext<ThemeContextType>({
  mode: 'light',
  toggleColorMode: () => {},
});

// Custom hook to use the theme context
export const useTheme = () => useContext(ThemeContext);

// Theme provider component
export const ThemeProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  // Get the saved theme from localStorage or use'light' as default
  const [mode, setMode] = useState<PaletteMode>(() => {
    const savedMode = localStorage.getItem('theme');
    return savedMode === 'dark' || savedMode === 'light' ? (savedMode as PaletteMode) : 'light';
  });

  // Function to toggle between light and dark mode
  const toggleColorMode = () => {
    setMode(prevMode => (prevMode === 'light' ? 'dark' : 'light'));
  };

  // Save the theme mode to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('theme', mode);
    document.documentElement.setAttribute('data-theme', mode);
  }, [mode]);

  // Create the theme based on the current mode
  const theme = useMemo(() => getTheme(mode), [mode]);

  // Provide the theme context and theme provider
  return (
    <ThemeContext.Provider value={{ mode, toggleColorMode }}>
      <MuiThemeProvider theme={theme}>{children}</MuiThemeProvider>
    </ThemeContext.Provider>
  );
};

